import { Component, OnInit } from '@angular/core';
import { ICliente } from './Models/cliente.model';
import { ApiService } from './services/api.service';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent implements OnInit {

  clientList: ICliente[] = [];

  constructor(private _apiService: ApiService) {}

  ngOnInit(): void {
      this._apiService.getAllClients().subscribe((data: ICliente[]) =>{
        this.clientList = data;
      })
  }
}

<div class="content" role="main">

  <h2 class="mb-4">Control de Clientes</h2>

  <div class="container mt-4">
    <table class="table">
      <thead>
        <tr>
          <th scope="col">Id</th>
          <th scope="col">Nombre</th>
          <th scope="col">Email</th>
          <th scope="col">Telefono</th>
          <th scope="col">Otros datos</th>
          <th scope="col"></th>
          <th scope="col"></th>
        </tr>
      </thead>
      <tbody>
        <tr *ngFor="let cliente of clientList">
          <th scope="row">{{cliente.ClienteId}}</th>
          <td>{{cliente.Nombre}}</td>
          <td>{{cliente.Email}}</td>
          <td>{{cliente.Telefono}}</td>
          <td>{{cliente.OtrosDatos}}</td>
          <td><button class="btn btn-primary">Detalle</button></td>
          <td><button class="btn btn-danger">Eliminar</button></td>
        </tr>

      </tbody>
    </table>

  </div>




</div>
